var turtle; // turtle pen

// draw branches
function createButterfly(x, y, col, size) {
  stroke(col);
  strokeWeight(size / 10);
  turtle.moveTo(x, y);

  let startAngle = round(random(180, 270));
  let extend = round(random(30, 90));
  turtle.turnTo(startAngle);

  turtle.penDown();
  for(let i = 1; i <= 4; i++) {
    let step = i <= 2 ? 1.5 : 1;
    let nextAngle = i % 2 === 0 ? - (90 + extend * 2) : extend;

    // draw a single wing
    for(let d = 0; d < 60; d++) {
      turtle.turnRight(1);
      turtle.moveForward(step * (size / 50));
    }
    turtle.turnRight(120);
    for(let d = 0; d < 60; d++) {
      turtle.turnRight(1);
      turtle.moveForward(step * (size / 50));
    }

    startAngle += nextAngle;
    turtle.turnTo(startAngle);
  }
  turtle.penUp();
}

function setup() {
  createCanvas(800, 600);

  turtle = new Turtle(); // create the turtle object
  turtle.penUp();
}

function draw() {
  background(0);
  gradientBackground(0, 0, width, 600, color('#A347FF'), color('#03045E'));

  for(let i = 0; i < height / 10; i++) {
    for(let a = 0; a < i * 5; a++) {
      createButterfly(abs(randomGaussian(0, width/3)), i * height / 10 + random(-height/10, height/10), color('hsla(' + round(random(360)) + ', 60%, 70%, ' + random(0.4, 1) + ')'), random(8, 12)); 
    }
  }

  noLoop();
}

function gradientBackground(x, y, w, h, col1, col2) {
  push();
  for(let i = 0; i < h; i += 0.1) {
    let cur_color = lerpColor(col1, col2, map(i, 0, h, 0, 1));
    stroke(cur_color);
    line(x, y + i, x + w, y + i);
  }
  pop();
}

