var head = new paper.Point(FEET_LENGTH + BODY_LENGTH, 300);
var tail = new paper.Point(FEET_LENGTH + OFFSET, 300);
var middle = new paper.Point(FEET_LENGTH + BODY_LENGTH / 2, 300);
// BODY
var wormBody = new paper.Path.Line(head, tail);
wormBody.insert(1, middle);
wormBody.strokeWidth = 15;
wormBody.strokeColor = '#adf542';
wormBody.strokeCap = 'round';
// FEET
var backFeet = new paper.Group()
var b = new paper.Path.Line(start, start + new paper.Point(FEET_LENGTH, 0));
b.strokeWidth = 15;
b.strokeColor = '#adf542';
b.strokeCap = 'round';
backFeet.addChild(b);
var feet = [];
for(var i = 0; i < 4; i++){
    feet[i] = new paper.Path.Circle(start + new paper.Point(7 * i, 8), 3);
    feet[i].strokeColor = '#adf542';
    feet[i].fillColor = '#adf542';
    backFeet.addChild(feet[i]);
}
var frontFeet = backFeet.clone();
frontFeet.position = head + new paper.Point(FEET_LENGTH / 2, 5);
// HEAD
var wormHead = new paper.Group();
var c = new paper.Path.Circle(end + new paper.Point(-2, -2), 10);
c.fillColor = '#adf542';
var e = new paper.Path.Circle(end + new paper.Point(0, -3), 3);
e.fillColor = 'black';
var m = new paper.Path.Line(end + new paper.Point(-2, 3), end + new paper.Point(6, 3));
m.insert(1, end + new paper.Point(3, 5));
m.smooth();
m.strokeColor = 'black';
wormHead.addChild(c);
wormHead.addChild(e);
wormHead.addChild(m);
// WHOLE WORM
var worm = new paper.Group();
worm.addChild(backFeet);
worm.addChild(wormBody);
worm.addChild(frontFeet);
worm.addChild(wormHead);


function onFrame(event){
  if(event.count % 32 === 0){
     walk();
     wormBody.smooth();
     backFront = !backFront;
  }
}

function walk(slope, direction){
    if(backFront){
     for(var i = 0; i < 100; i++){
       wormHead.position += new paper.Point(INC, 0);
       frontFeet.position += new paper.Point(INC, 0);
       wormBody.firstSegment.point += new paper.Point(INC, 0);
       wormBody.segments[1].point += new paper.Point(0.5, YINC);
        }
      }else{
          for(var i = 0; i < 100; i++){
              backFeet.position += new paper.Point(INC, 0);
              wormBody.lastSegment.point += new paper.Point(INC, 0);
              wormBody.segments[1].point += new paper.Point(INC - 0.5, -YINC);
          }  
      }

}

