function tile() {



	let marginW = width / 6;
	let marginH = height / 6;
	let w = (width - marginW * 2) / CELLS;
	let h = (height - marginH * 2) / CELLS;

	for (var j = 0; j < CELLS; j++) {
		for (var i = 0; i < CELLS; i++) {
			let x = marginW + i * w;
			let y = marginH + j * h;

			motifs(x + w / 2, y + h / 2, w, h);

		}
	}
	t += vel;
}


function motifs(x, y, w, h) {

	push();
	translate(x, y)


	if (dist(mouseX, mouseY, x, y) < h / 2) {
		rotate(t)

	}


	 fill(random(palette));

	rect(0, 0, CELL_SIZE, CELL_SIZE);
	pop();
}

