function draw() {
  background(0);
  for (let i =0; i<numP; i++){
   let p = new Particle(); 
      particles.push(p);

  }

  for (let i = particles.length -1; i>=0; i--) {
    particles[i].update();
    particles[i].show();
    if (particles[i].finished()){

      particles.splice(i,1);
    }
  }
}

class Particle {

  constructor () {
    this.x = width/2;
    this.y = height/2;
    this.vx = random(-1, 1);
    this.vy = random(-1, 1);
    this.ax = random(-0.1, 0.1);
    this.ay = random(0.1, -0.1);
    this.alpha = 255;
  }

  finished(){
    return this.alpha <0;
  }

  update(){
    this.vx += this.ax;
    this.vy += this.ay;
    this.x += this.vx;
    this.y += this.vy; 
    this.alpha -=5;
  }

  show() {
    noStroke();
//stroke(255);
    fill(255, this.alpha);
    ellipse(this.x+random(-(width/2-mouseX), width/2-mouseX), this.y+random(-(height/2-mouseY), height/2-mouseY), sizeP);
   // text ("0", this.x, this.y);
  }
}

lentil_offset = 1.5;

// the lentil form
module lentil(radius){
   r = 139 + rands(-10, 10, 1)[0];
   g = 69 + rands(-10, 10, 1)[0];
   b = 19 + rands(-10, 10, 1)[0];
   color([r / 255, g / 255, b / 255]){
     translate([0, 0, -radius / 2]) intersection(){ 
       sphere(radius); // , $fn=100);
       translate([0, 0, radius * lentil_offset]) sphere(radius); // , $fn=100);
     }
   }
}


module cone(start, stop, radius, direction){
  for(levelRadius = [start * radius : radius / 2 : stop * radius]){
    radialIncrement = radius * 360 / (1.7 * PI * (levelRadius));
    for(i = [0 : radialIncrement : 360]){
       zDir = direction ? -1 : 1;
       window = rands(0, 1, 1)[0];
       if(window <= 0.85){
         translate([levelRadius * cos(i), levelRadius * sin(i), zDir * radius / 2 * levelRadius / radius]) lentil(radius); 
       }
    }
  }
}

module tower(start, stop, radius, direction){
  levelRadius = 1.2 * radius;
  for(height = [start * radius / 2: radius / 2 : stop * radius / 2]){
    radialIncrement = radius * 360 / (1.9 * PI * (levelRadius));
    for(i = [0 : radialIncrement : 360]){
       zDir = direction ? -1 : 1;
       window = rands(0, 1, 1)[0];
       if(window <= 0.85){
         translate([levelRadius * cos(i), levelRadius * sin(i), zDir * height]) lentil(radius);
       }
    }
  }

}


