class Walker{
  constructor(dir){
    this.pos = createVector(0,0)
    this.dir = dir
  }

  update(sw){
    let x = this.pos.x
    let y = this.pos.y
    let rAngle = noise(x, y, millis()/10000)
    rAngle = map(rAngle, 0,1, 0, 360)
    this.dir.rotate(rAngle)
    this.pos.add(this.dir.setMag(20))
    strokeWeight(sw)
    stroke(random(100, 150),noise(sw),  noise(sw, rAngle))
    point(this.pos)
  }
}

