  let size = 70 - offset;
  stroke(255, 0, 0, 60);
  strokeWeight(0.5);
  noFill();
  textFont("Arial");
  textSize(size);
  text("comp", 50 + sin(a) * 10, height / 2 + 50);
  text("form", 700 + (1 - sin(a)) * 10, height / 2 + 50);
}

