// Inspired by Jackson Pollock's paintings

let colors = ["#ed3441", "#ffd630", "#329fe3", "#154296", "#303030", '#f0f0f0', "#213647", "#2ec4b6", "#c4071d", "#ff9f1c", "#247ba0"];

let maxFrames;
let minTight;

function setup() {
  createCanvas(600, 600);
  //frameRate(15);
  //noLoop();


  maxFrames = 150;
	minTight = random(-1, 0.5);

  background(colors.splice(random(colors.length), 1)[0]);
}

function draw() {
  if (random() < 0.6) {
    drawLine();
  }
  if (random() < 0.8) {
    drawDrop();
  }
  if (frameCount > maxFrames) {
    noLoop();
  }
}

function drawLine() {
  stroke(random(colors));
  noFill();
  let n = random(5, 15);
  let points = [];

  let w = random(width/4, 3 * width/4);
  let h = random(height/4, 3 * height/4);
  let x0 = random(-width/3, width - w + width/3);
  let y0 = random(-height/3, height - h + height/3);
  translate(x0 + w/2, y0 + h/2); 
  //let theta = random(TWO_PI);
  //rotate(theta);

	let x = random(-w/2, w/2);
	let y = random(-h/2, h/2);
  for (let i = 0; i < n; i++) {
    points.push(createVector(x, y));
		if (i % 2 === 0) {
			y = random(-h/2, h/2);
		} else {
			x = random(-w/2, w/2);
		}
  }
  for (let i = 0; i < n; i++) {
    strokeWeight(random(1, 2.5));
		curveTightness(random(minTight, minTight + 0.2));
    beginShape();
    for (let p of points) {
      let nzx = noise(p.x, p.y, frameCount + i/4);
      let nzy = noise(p.x, p.y, frameCount + i/4 + 1);
      curveVertex(p.x + (nzx - 1/2)*10, p.y + (nzy - 1/2)*10);
    }
    endShape();
  }
}

function drawDrop() {
  fill(random(colors));
  noStroke();

  let size = randomGaussian(8, 2);
  let xCenter = random(width);
  let yCenter = random(height);
  let maxNoise = randomGaussian(3, 8);

  //Dots
   beginShape();
  let angleStep = TWO_PI / floor(random(5, 10));
  for (let theta = 0; theta <= TWO_PI + 3 * angleStep; theta += angleStep) {
    let r1, r2;
		r1 = cos(theta)+1;
		r2 = sin(theta)+1;
    let r = size + noise(r1*2, r2*4, frameCount) * maxNoise;
    let x = xCenter + r * cos(theta);
    let y = yCenter + r * sin(theta);
    curveVertex(x, y);
  }
  endShape();
}

