function face(x, y) {
  t.penUp();
  t.moveTo(x, y);
  t.penDown();
  // draw the triangle
  for (let i = 0; i < 3; i++) {
    t.moveForward(280);
    t.turnRight(120);
  }
}


function ear(x, y, r) {
  push();
  rotate(r);
  t.penUp();
  t.moveTo(x, y);
  t.penDown();
  // draw the triangle
  for (let i = 0; i < 3; i++) {
    t.moveForward(80);
    t.turnRight(120);
  }
  // draw the triangle
  t.penUp();
  t.moveTo(x + 20, y + 10);
  t.penDown();
  for (let i = 0; i < 3; i++) {
    t.moveForward(40);
    t.turnRight(120);
  }
  pop();
}

function whiskers(x, y, r) {
  push();
  rotate(r);
  t.penUp();
  //
  t.moveTo(x, y);
  t.penDown();
  t.moveForward(80);
  t.penUp();
  //
  t.moveTo(x, y + 20);
  t.penDown();
  t.turnRight(20);
  t.moveForward(80);
  t.penUp();
  //
  t.moveTo(x, y - 20);
  t.penDown();
  t.turnLeft(40);
  t.moveForward(80);
  t.penUp();
  pop();
}

function cir(x,y,s){
  push();
  t.penUp();
  t.moveTo(x,y);
  t.penDown();
  for (var i = 0; i < 36; i++) {
    t.turnLeft(10);
    t.moveForward(4*s);
  }
  pop();
}

