  for (let t = 0; t < 1; t += step) {
    x21 = lerp(x1, x2, t);
    x22 = lerp(x2, x3, t);
    x23 = lerp(x3, x4, t);
    y21 = lerp(y1, y2, t);
    y22 = lerp(y2, y3, t);
    y23 = lerp(y3, y4, t);

    x31 = lerp(x21, x22, t);
    x32 = lerp(x22, x23, t);
    y31 = lerp(y21, y22, t);
    y32 = lerp(y22, y23, t);

    x41 = lerp(x31, x32, t);
    y41 = lerp(y31, y32, t);
    let v = createVector(x32 - x31, y32 - y31);

    v.normalize();
    let vn = createVector(-v.y, v.x);
    let p = createVector(x41, y41);

    //Hardcode the thickness below as you like
    stroke(150, noise(v1.x, v1.y) * 255, noise(v3.x, v3.y) * 255);
    strokeWeight(
      thickness.value() / 2 + noise(-millis() / 120 + pointCt / 8) * 4
    );
    p.add(vn.mult(thickness.value() + noise(millis() / 150 + pointCt / 8) * 4));
    point(p.x, p.y);

    pointCt++;
  }
}

