function draw() {
  if (startMillis == null) {
    startMillis = millis();
  }
  var duration = 3000;

  var elapsed = millis() - startMillis;
  var t = map(elapsed, 0, duration, 0, 1);

  if (t > 1) {
    noLoop();
    console.log("finished recording.");
    capturer.stop();
    capturer.save();
    return;
  }

  // ACTUAL DRAW FUNCTIONS HERE

  if (frameCount <= textFrameCount) {
    randerText();
