function Deck(min, max, inc, values){

   if(values === undefined){
    this.valueArray = [];
    for(let i = min; i < max; i += inc){
      this.valueArray[i] = i;
    }
   }else{
     this.valueArray = values;
   }

  this.position = 0;

  console.log(this);
}

Deck.prototype.shuffle = function(){
  this.valueArray = shuffle(this.valueArray);
  console.log(this);
}

Deck.prototype.getValue = function(){
  // find the value at the current position in the deck
  var v = this.valueArray[this.position];

  // change the position for next time
  this.position++;

  // if that was the last value, shuffle and start over from the top
  if (this.position == this.valueArray.length) {
    this.valueArray = shuffle(this.valueArray);
    this.position = 0;
  }

  // return the value
  return v;
}

