// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js
// Click the canvas to regenerate the pattern

var _row = 7, _col = 12; // amount of the row and the column
var _singHeight = 0, _singRow = 6;

var _patternMode = 0;
var _patternHis = [];
// 2 patterns for single block - pure & hybrid
// 0: pure only emergy separately
// 1-4: hybrid can emergy continuously but never more than 4 times

// Color Plan 1:
var _hybridColors = ['#626262', '#a79661', '#1f201f']
var _pureColors = ['#d7d7d7', '#5f6888', '#d2c18d'];
// Color Plan 2:
// var _hybridColors = ['#626262', '#c44032', '#2d2e35']
// var _pureColors = ['#dbd3cb', '#987fa4', '#d1ae8b'];


function setup() {
  createCanvas(480, 640);
  noStroke();
  _singHeight = height / _row / _singRow;

  noLoop();
}

function draw() {
  background(0);

  // draw pattern
  drawPattern();
}

function drawPattern() {
  for(let r = 0; r < _row; r++) {
    for(let c = 0; c < _col; c++) {      
      // choose the pattern
      switch(_patternMode) {
        case 0:
          _patternMode ++;
          break;
        case 1:
        case 2:
        case 3:
          if(r > 0) {
            if(c < _col - 1 && !patternCheck(r - 1, c + 1)) {
              _patternMode = 0;
            } else if(!patternCheck(r - 1, c)) {
              _patternMode ++;
            } else {
              if(random(10) < 8) _patternMode ++;
              else _patternMode = 0;
            }
          } else {
            if(random(10) < 8) _patternMode ++;
            else _patternMode = 0;
          }
          break;
        case 4:
          _patternMode = 0;
          break;
        default:
          console.log("Invalid pattern number.")
      }
      _patternHis.push(_patternMode);

      drawSingPattern(r, c, _patternMode);
    }
  }
}

function drawSingPattern(row, col, pattern) {
  let x = col * (width / _col), y = row * (height / _row);

  // check the pattern
  if(pattern !== 0) { // hybrid pattern
    // assign random colors
    let deck = shuffle(_hybridColors);
    let odd_color = deck[0], even_color = deck[1];

    for(let i = 0; i < _singRow; i++) {
      if(i % 2) { // odd
        fill(odd_color);
      } else { // even
        fill(even_color);
      }
      rect(x, y + i * _singHeight, width/_col, _singHeight);
    }
  } else { // pure pattern
    // assign random color
    let color = _pureColors[floor(random(_pureColors.length))];

    fill(color);
    rect(x, y, width/_col, height/_row);
  }
}

// check a specific pattern, return the pattern mode
// (true: hybrid; false: pure)
function patternCheck(row, col) {
  if(_patternHis[row * _col + col] !== 0) return true;
  else return false;
}

function mousePressed() {
  _patternHis = [];
  console.log(_patternHis);
  drawPattern();
}

