// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js
let n = 20;
let colors = [[255], [0]];

function setup() {
  createCanvas(500, 500);
  noStroke();
}

function draw() {
  background(colors[0]);
  let t = frameCount/100;

  let s = width/n;
  for (let i = 0; i < n; i++) {
    for (let j = 0; j < n; j++) {
      let x = i*s, y = j*s;
			let r = noise(i, j, t);
			let thresh = (sin(dist(x, y, width/2, height/2)/100-t)+1)/2
      if ((i+j)%2 == 0) {
        if (r < thresh) {
          fill(colors[1]);

        } else {
          fill(colors[1]);
          square(x, y, s);
          fill(colors[0]);

        }
      } else {
        if (r < thresh) {
          fill(colors[1]);

        } else {
          fill(colors[1]);
          square(x, y, s);
          fill(colors[0]);

        }
      }
    }
  }
}

