function setup() {
	createCanvas(500, 500);
}

function draw() {
	optFunc()
} 

function optFunc() {
	img = createImage(500, 500);
	img.loadPixels();

	for (let y = 0; y < img.height; y++) {
		for (let x = 0; x < img.width; x++) {
			let k = int(noise(x / 20, y / (100 - sin(frameCount / 100)*50), frameCount / 30) + 0.45)
			let f = int(noise(x / 30, y / (200 + cos(frameCount / 100)*100) + 50, frameCount / 30 + 50) + 0.55)
			let g = int(noise(x / 50, y / 100 + 300, frameCount / 30 + 300) + 0.45)
			let a = [255, 255, 255]
			let z = int(random(5))

			if (k === 1) {
				a = [255, 255, 255]
			} else if (f === 1) {
				a = [255, 0, 0]
			} else if (g === 1) {
				a = [0, 0, 255]
			} else {
				a = [0, 0, 0]
			}

			let c = [a[0], a[1], a[2], 255]

			setQuick(img, x, y, c);
		}
	}

	img.updatePixels();

	noSmooth();
	image(img, 0, 0, width, height);
	// noLoop()
}

function setQuick(img, x, y, c) {
	const i = (y * img.width + x) * 4;

	img.pixels[i + 0] = c[0];
	img.pixels[i + 1] = c[1];
	img.pixels[i + 2] = c[2];
	img.pixels[i + 3] = c[3];
}

