const points = [];
let pic;

function preload() {
	pic = loadImage('https://munusshih.github.io/fonts/pic.jpg');
}

function setup() {
	createCanvas(500, 500);
	pic.loadPixels();
}

function draw() {
	background(0);
	noStroke();

	let pixel = 5
	let randomStart = frameCount

	for (i = 0; i < pic.width; i += pixel) {
		for (j = 0; j < pic.height; j += pixel) {

			let x = int(i + noise(randomStart/100, 0, i/100 + randomStart/50) * width)%pic.width
			let y = int(j + noise(randomStart/100, 1, j/80 + randomStart/100) * height)%pic.height

			fill(extractColor(x, y))
			rect(i, j, 50)
		}
	}

}

function extractColor(x, y) {
	offset = ((y * pic.width) + x) * 4;

	let r = pic.pixels[offset + 0];
	let g = pic.pixels[offset + 1];
	let b = pic.pixels[offset + 2];

	return color(r, g, b)
}

