// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js

let frequency_slider;
let amplitude_slider;
let timeSpeed_slider;

var amount = 300;

const lineAmount = 15;
const lines = [];

function setup() {
  createCanvas(500, 300);
  colorMode(HSB, 1);
  rectMode(CENTER);

  createP("Frequency");
  frequency_slider = createSlider(0, 100, 50)
  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);
  createP("Time Speed");
  timeSpeed_slider = createSlider(0, 100, 50);

  // create lines
  let diagonal = sqrt(sq(width) + sq(height)) / 2;
  for(let l = 0; l < lineAmount; l++) {
    let direct = l / lineAmount * TWO_PI + (random(TWO_PI / lineAmount) - TWO_PI / lineAmount);
    lines.push({ num: l,
      x: width/2 + diagonal * cos(direct),
      y: height/2 + diagonal * sin(direct)
    });
  }
}

function draw() {
  background(1, 0, 0);
  ellipseMode(CENTER);
  noStroke();
  // noiseDetail(1, 0.5);

  let frequency = frequency_slider.value() / 5;
  let amplitude = amplitude_slider.value() / 10;
  let speed = timeSpeed_slider.value() / 100;

  let count = frameCount / 20;
  amount = round(200 * (speed + amplitude / 10));

  lines.forEach((line) => drawLine(line,frequency, amplitude, speed, count));
}

function drawShape(frequency, amplitude, speed, count) {
  beginShape();
  for (i = 0; i < col; i++) {
    let scale = i / col;
    let x = lerp(startX, endX, scale);
    let y = lerp(startY, endY, scale);

    let offsetX = (noise(count * speed + scale * frequency, 0) - 0.5) * amplitude * 12;
    let offsetY = (noise(count * speed + scale * frequency, 1000) - 0.5) * amplitude * 12;

    curveVertex(x + offsetX, y + offsetY);
    // for(j = 0; j < row; j++) {
    //   // fill(noise(count * speed + scale * frequency, amplitude * j), 1, 1);
    //   fill(120, noise(count * speed + scale * frequency, amplitude * j) * 255);
    //   rect(i * width / col, j * height / row, width/50, height/row);
    //   // ellipse(i * width/ col, j * height / row, 20);
    // }
  }
  endShape();
}

function drawLine(line, frequency, amplitude, speed, count) {
  for (i = 0; i < amount; i++) {
    let scale = i / amount;
    let x = lerp(line.x, width / 2, scale);
    let y = lerp(line.y, height / 2, scale);

    let offsetX = (noise(count * speed + scale * frequency, line.num) - 0.5) * amplitude * 12;
    let offsetY = (noise(count * speed + scale * frequency, 1000 + line.num) - 0.5) * amplitude * 12;

    fill((line.num / lines.length + count) % 1, 1, map(noise(count * speed + scale * frequency), 0, 1, 0.25, 1));
    // fill(1, 0, noise(count * speed + scale * frequency, line.num));

    ellipse(x + offsetX, y + offsetY, map(i, 0, amount, 50, 2));
