      beginShape();
        for (let i = 0; i < 360; i++) {
          let noiseFactor = noise(i * 0.01 * j, frameCount/250);

          let x = radius/j * cos(i) * noiseFactor;
          let y = radius/j * sin(i) * noiseFactor;

          curveVertex(x, y)    
        }
