	noiseStrength = 5,
	numbers = 200
let colorList = ["#F21D2F", "#D971AA", "#2976A6", "#F2A057", "#F28D52", "#400101", "#F2F2F2"]
let bg, brusher
let slider
function setup() {
	createCanvas(700, 700);
	noStroke()
	bg = color("#F2EDD0")
	background(bg)
	brusher = min(random(5, 20), random(5, 20))
	noiseStrength = random(3, 10),
	numbers = random(50, 1000)

	for (let i = 0; i < numbers; i++) {
		corals[i] = new Coral(i)
	}

	slider = createSlider(0, 8, 3,0);
	slider.position(0, 720)
}

function draw() {

	for (let i = 0; i < numbers; i++) {
		corals[i].draw()
		corals[i].update()
	}
}

class Coral {
	constructor(i) {
		this.x = random(-100, width + 100)
		this.y = random(-100, height + 100)
		this.angle = 0
		this.fillColor = random(colorList)
		this.stepSize = random(2, 4)
		this.note = i
		this.time = 0
		this.finalTime = 50
		this.brushSize = brusher*(random(0.9, 1.2)+random(1.0, 1.4))/2
	}

	update() {
		if (this.x < -100 || this.y < -100 || this.x > width+100 || this.y > height+100 || this.time == 50) {
			this.x = random(-100, width + 100)
			this.y = random(-100, height + 100)
			this.time = 0
		}
		this.angle = noise(this.x / noiseScale,
			this.y / noiseScale,
			millis()/pow(10, slider.value())) * noiseStrength;
		this.x += cos(this.angle) * this.stepSize + noise(this.note + frameCount / 10);
		this.y += sin(this.angle) * this.stepSize + noise(this.note + frameCount / 100);
		this.time++
	}

	draw() {
		fill(this.fillColor)
		circle(this.x, this.y,
			(sin(this.time / (50 / 3)) * this.brushSize))
	}
}

