// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js
let angle = 0;
let offset = 100;
let scalar = 80;
let speed = 0.05;

function setup() {
  createCanvas(400, 400);
  pixelDensity(1);
  angleMode(RADIANS);
}

function draw() {
  background(51);

  loadPixels();
  for (var y = 0; y < height; y++) {
    for (var x = 0; x < width; x++) {
      var a = offset + sin(angle) * scalar; 
      var b = offset + cos(angle + 0.4) * scalar; 
      var c = offset + sin(angle + 0.8) * scalar; 

      var index = (x + y * width) * 4;
      pixels[index + 0] = random(c) * 10;
      pixels[index + 1] = random(a) * 10;
      pixels[index + 2] = random(b) * 10;
      pixels[index + 3] = 255;
    }
  }
  angle +=speed;
  updatePixels();
}

