function setup() {
	createCanvas(500, 500);
}

function optFunc() {
	img = createImage(500, 500);
	img.loadPixels();

	for (let y = 0; y < img.height; y++) {
		for (let x = 0; x < img.width; x++) {
			let k = tan((y + frameCount*params.z)  / pow(y+x, 
			-params.c*abs(sin(0.5 + params.a*noise(x/1000) - params.b*noise(y/1000)))*params.y/params.x)) * 255
			let c = [k, k, k, 255]

			setQuick(img, x, y, c);
		}
	}

	img.updatePixels();

	noSmooth();
	image(img, 0, 0, width, height);
}

function setQuick(img, x, y, c) {
	const i = (y * img.width + x) * 4;

	img.pixels[i + 0] = c[0];
	img.pixels[i + 1] = c[1];
	img.pixels[i + 2] = c[2];
	img.pixels[i + 3] = c[3];
}

// require https://cdn.jsdelivr.net/npm/tweakpane@3.0.7/dist/tweakpane.min.js
// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js
// require /turtles/turtle/turtle.js

const pane = new Tweakpane.Pane();

const PARAMS = {
  generations: 8,
  lineLenght: 10,
  adjustAngle: 0,
  system: 0,
};

let currentStep = 0;
let startString = "";
let drawString = "";
let t = null;

function setup() {
  createCanvas(800, 800);

  frameRate(1);

  pane.addInput(PARAMS, "generations", {
    step: 1,
    min: 1,
    max: 12,
  });
  pane.addInput(PARAMS, "system", {
    options: {
      "Sierpinski triangle": 0,
      "SierpiÅ„ski arrowhead curve": 1,
      "Koch curve": 2,
    },
  });

  pane.addInput(PARAMS, "lineLenght", {
    min: 1,
    max: 50,
  });
  pane.addInput(PARAMS, "adjustAngle", {
    min: -90,
    max: 90,
  });

  pane.on("change", adjustValues);

  t = new Turtle();
  adjustValues();
}

const adjustValues = () => {
  currentStep = 0;
  loop();

  switch (PARAMS.system) {
    // Sierpinski triangle
    case 0:
      startString = "F-G-G";
      break;
    // SierpiÅ„ski arrowhead curve
    case 1:
      startString = "F";
      break;
    // Koch curve
    case 2:
      startString = "F";
      break;
  }

  drawString = startString;
};

const applyLSystem = (string) => {
  let newString = "";

  switch (PARAMS.system) {
    // Sierpinski triangle
    case 0:
      // Taken from Wikipedia
      /*
      variables : F G
      constants : + âˆ’
      start  : Fâˆ’Gâˆ’G
      rules  : (F â†’ Fâˆ’G+F+Gâˆ’F), (G â†’ GG)
      angle  : 120Â°
      */
      newString = string.split("").map((c) => {
        switch (c) {
          case "F":
            return "F-G+F+G-F";
            break;
          case "G":
            return "GG";
            break;
          default:
            return c;
            break;
        }
      });

      break;
    // SierpiÅ„ski arrowhead curve
    case 1:
      newString = string.split("").map((c) => {
        switch (c) {
          case "F":
            return "G-F-G";
            break;
          case "G":
            return "F+G+F";
            break;
          default:
            return c;
            break;
        }
      });

      break;
    // Koch curve
    case 2:
      newString = string.split("").map((c) => {
        switch (c) {
          case "F":
            return "F+F-F-F+F";
            break;
          default:
            return c;
            break;
        }
      });
      break;
  }

  return newString.join("");
};

const drawLSystem = (string) => {
  let L_SYSTEM_ANGLE = 0;

  switch (PARAMS.system) {
    // Sierpinski triangle
    case 0:
    case 1:
      L_SYSTEM_ANGLE = (PARAMS.system === 0 ? 120 : 60) + PARAMS.adjustAngle;

      for (let i = 0; i < string.length; i++) {
        switch (string.charAt(i)) {
          case "F":
          case "G":
            t.moveForward(PARAMS.lineLenght);
            // console.log("moved forward")
            break;
          case "+":
            t.turnLeft(L_SYSTEM_ANGLE);
            // console.log("turned left")
            break;
          case "-":
            t.turnRight(L_SYSTEM_ANGLE);
            // console.log("turned right")
            break;
        }
      }

      break;
    case 2:
      L_SYSTEM_ANGLE = 90 + PARAMS.adjustAngle;

      for (let i = 0; i < string.length; i++) {
        switch (string.charAt(i)) {
          case "F":
            t.moveForward(PARAMS.lineLenght);
            // console.log("moved forward")
            break;
          case "+":
            t.turnLeft(L_SYSTEM_ANGLE);
            // console.log("turned left")
            break;
          case "-":
            t.turnRight(L_SYSTEM_ANGLE);
            // console.log("turned right")
            break;
        }
      }

      break;
  }
};

function draw() {
  background("black");
  stroke("white");
  noFill();

  t.penUp();
  t.moveTo(50, height / 2);
  t.turnTo(0);
  t.penDown();

  // let drawString = startString;

  // for (let i = 0; i < currentStep; i++) {
    drawLSystem(drawString);
    drawString = applyLSystem(drawString);
  // }

  currentStep++;

  // end drawing
  if (currentStep >= PARAMS.generations) {
    noLoop();
  }
}

