let slider;

function setup() {
  createCanvas(500, 500);
  slider = createSlider(-255, 255, 100);
  slider.position(500, 10);
  slider.style('width', '80px');
}

function draw() {
  background(255);

  img = createImage(99, 99);
  img.loadPixels();

  for(let i = 0; i<img.pixels.length; i++){
    img.pixels[i] = noise(i + frameCount/50) * 108 + 150
  }
  // console.log(img.pixels)
  for (let y = 0; y < img.height; y++) {
    for (let x = 0; x < img.width; x++) {
      let distToC = pow((x-img.width/2),2) + pow((y-img.height/2),2)

      if(distToC%40 < (x+y)/3.6 * (0.5+1+sin(frameCount/100))){
        img.pixels[y*img.width *4 + x *4 ] -= 80
        img.pixels[y*img.width *4 + x *4 +1] -= 40
        img.pixels[y*img.width *4 + x *4 +2] += 200

      }else{
        img.pixels[y*img.width *4 + x *4 ] += x/img.width * 180 -y/img.width * 180
        img.pixels[y*img.width *4 + x *4 +1] += y/img.height * 180 -x/img.width * 180
        img.pixels[y*img.width *4 + x *4 +2] -= slider.value()
        img.pixels[y*img.width *4 + x *4 +3] -= 80
      }



    }
  }

  img.updatePixels();

  noSmooth();
  image(img, 0, 0, width, height);
  // noLoop();
}

// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js
// I followed The Coding Train's video "Coding Challenge #136.1: Polar Perlin Noise Loops" https://www.youtube.com/watch?v=ZI1dmHv3MeM and "Coding Challenge #36: Blobby!" https://www.youtube.com/watch?v=rX5p-QRP6R4to create this sketch

let noiseMax = 0.5;
let slider, colSlider;
let zOff = 0;

function setup() {
  createCanvas(600, 600);
  colorMode(HSB);
  // frameRate(30);

  slider = createSlider(0, 20, 10, 0.1);
  colSlider = createSlider(0, 240, 150, 0.5);
}

function draw() {
  background(0);

  noFill();
  stroke(100);
  strokeWeight(0.8);

  noiseMax = slider.value();
    translate(width/2, height/2);

  // density of lines changed by n in i+= n. 0.1 runs smoothly, but 0.02 looks super cool
  for(let i = 0; i < 1.5; i+=0.05){
    push();
      scale(i);
      stroke(0 + i*colSlider.value(), 100, 100);
      drawBlobs();
    pop();
  }
  // noLoop(); // When this is on it generates a new flower each time
}

function drawBlobs(){
  push();
  // translate(width/2, height/2);
  beginShape();
  for (let a = 0; a < TWO_PI; a+=0.05){
    let xOff = map(cos(a), -1, 1, 0, noiseMax);
    let yOff = map(sin(a), -1, 1, 0, noiseMax);
    let r = map(noise(xOff, yOff, zOff), 0, 1, 80, 200);
    let x = r * cos(a);
    let y = r * sin(a);
    vertex(x, y);

  }
  endShape(CLOSE);
  zOff += 0.01;
  pop();
}

