class Block {
  constructor(x, y, block, size) {
    this.x = x;
    this.y = y;
    this.block = block;
    this.size = size;
    this.img = createImage(block, block);
    this.img.loadPixels();

    var d = dist(x, y, 250, 250);
    var hue = (360 * d) / dist(0, 0, 250, 250);

    for (var col = 0; col < block; col++) {
      for (var row = 0; row < block; row++) {
        let c = color(hue, 90, random(20, 90));
        this.img.set(col, row, c);
      }
    }
    this.img.updatePixels();
  }

  display() {
    image(this.img, this.x + 10, this.y + 10, this.size, this.size);
  }
}

var blockList = [];
function setup() {
  colorMode(HSL);
  createCanvas(560, 560);
  let size = 40;
  let pixel = 3;
  let spacing = 10;
  let count = width / (size + spacing);
  for (i = 0; i < count; i++) {
    for (let j = 0; j < count; j++) {
      var block = new Block(
        i * (size + spacing),
        j * (size + spacing),
        pixel,
        size
      );
      blockList.push(block);
    }
  }
}

function draw() {
  background(0, 0, 10);
  noSmooth();
  for (let block of blockList) {
    block.display();
  }
}

