//initialize map and fill with color
function controlMap(){

  mapping = [];

  for (let x = 0; x < windowWidth; x++){
    mapping[x] = [];
	for (let y = 0; y < windowHeight; y++){
      mapping[x][y] = pickTerrain(x, y);
    }
  }

}

//pick colors
function pickTerrain(x, y){

  let h = noise((x)*noiseScale, (y)*noiseScale);

  let c = "#ff0022";

  if (h < 0.2){  c = ocean; } 
  else if (h < 0.3) { c = shore; } 
  else if (h < 0.4) { c = sand; }
  else if (h < 0.58) { c = grass; }
  else if (h < 0.65) { c = swamp; }
  else if (h < 0.6) { c = stone; }
  else if (h < 0.7) { c = mountain; } 
  else { c = snow; }

  return color(c);

}

//show map
function drawMap(){

  for (let x = 0; x < windowWidth; x++){
	for (let y = 0; y < windowHeight; y++){
      set(x, y, mapping[x][y])
    }
  }
  updatePixels();

}

