		background(255)
	}
	for (let i = 0; i < walker.length; i++) {
		walker[i].draw()
		walker[i].walk()
	}
}

class Walker {
	constructor() {
		this.color = random(colorList)
		this.currentRadius = random(300);
		this.currentAngle = max(random(360), random(360));
		this.currentSize = min(random(20), random(20));
		this.radWalk = (random(-3, 3) + random(-3, 3)) / 2;
		this.angWalk = random(-0.03, 0.03);
		this.sizeWalk = (random(-3, 3) + random(-3, 3)) / 2;
	}
	draw() {
		fill(this.color)
		ellipse(width / 2 + this.currentRadius * cos(this.currentAngle),
			height / 2 + this.currentRadius * sin(this.currentAngle), this.currentSize)
	}
	walk() {
		if (this.currentRadius + this.radWalk < 300) {
			this.currentRadius += this.radWalk
			this.currentAngle += this.angWalk
		}
		if (this.currentSize + this.sizeWalk > 0 && this.currentSize + this.sizeWalk < 20) {
			this.currentSize += this.sizeWalk
		}
		this.radWalk = (random(-3, 3) + random(-3, 3)) / 2;
		this.angWalk = random(-0.03, 0.03);
		this.sizeWalk = (random(-3, 3) + random(-3, 3)) / 2;
	}
}

