  music.showControls();

  amp = new p5.Amplitude();
  amp.setInput(music);
  fft = new p5.FFT(0.95, 64);
  fft.setInput(music);
}

function draw() {
  background(0);
  translate(width/2, height/2);

  let t = frameCount / 100;

  // get the sound parameters
  const volume = amp.getLevel();
  const spectrum = fft.analyze();

  // draw the sun
  let sunSize = volume * 4 + 10;

  let radius = sunSize * 4;
  let nlayer = round(sunSize) * 4;
  let interval = 1;
  let hue = map((frameCount / 2) % 360, 0, 360, 0, 1);
  drawSun(nlayer, interval, radius, spectrum, t, hue);
}

function drawSun(nlayer, inter, radius, freqs, t, color) {
  let n = nlayer, step = 0.01, kMax = 2;
  let minRadius = radius, interval = inter;
  for (let i = 2 * n; i > 0; i--) {
	let alpha = 1 - (i / (2 * n));
	fill((alpha/10 + color) % 1, 1, alpha * 1.5, 1);
	let size = minRadius + i * interval;
	let k, noiseScale;
	if (i < n) {
	  k = kMax * sqrt(i/n);
	  noiseScale = i/n;
	} else {
	  k = kMax * sqrt(1 - (i - n)/n);
	  noiseScale = 1 - (i - n)/n;
	}
    drawBlob(size, 0, 0, k, t - i * step, noiseScale, freqs);
  }
}

function drawBlob(size, xCenter, yCenter, k, t, scale, freqs) {
  beginShape();
  let angleStep = 360 / freqs.length;
  for (let i = 0, theta = 0; i < freqs.length; i++, theta += angleStep) {
	let r1 = cos(theta) + 1, r2 = sin(theta) + 1;
    let freq = map(freqs[i], 0, 255, 0, 200);
    let r = noise(k * r1,  k * r2, t / 8) * (size + freq * scale);
    let x = xCenter + r * cos(theta);
    let y = yCenter + r * sin(theta);
    curveVertex(x, y);
  }
  endShape(CLOSE);
}

// smooth an array
function smoothen(arr) {
  let sum = 0, newArr = [];
  for(let i of arr) sum += i;
  let average = sum / arr.length;
  for(let i of arr) {
    let diff = sqrt(abs(i - average));
    let newItem = i >= average ? average + diff : average - diff;
    newArr.push(newItem);
  }
  return newArr;
}

