    let to = color(colors[index + 1]); // color(hueB, 1000, 1000);

    for (let i = 0; i < int(params.gradient); i++) {
      let now = lerpColor(from, to, i / params.gradient)
      let currentRad = radius - (params.maxRad / params.number) * (i / params.gradient)
      // if(i == 0){
