//Declare colorFromDeck function
function colorFromDeck(theme) {
    let v = colors[theme][posColor];
    posColor++;
    if (posColor == colors[theme].length) {
        colors[theme] = shuffle(colors[theme]);
        posColor = 0;
    }
    return v;
}

