function frame(x,y){
translate(width/2,height/2);
// stroke(255, 0, 0);
fill(0,0,0);
beginShape();
// Exterior part of shape, clockwise winding
vertex(-x, -x);
vertex(x, -x);
vertex(x, x);
vertex(-x, x);
// Interior part of shape, counter-clockwise winding
beginContour();
vertex(-y, -y);
vertex(-y, y);
vertex(y, y);
vertex(y, -y);
endContour();
endShape(CLOSE);
}

const PHI = (1 + Math.sqrt(5)) * 0.5;
const dotSize = 0.03;
const radius = Math.sqrt(0.5) + dotSize;
let t = 0;
let old_t;
const frames = 1000;
let sceneID = 1;

function setup() {
  windowWidth > windowHeight ? 
    createCanvas(windowWidth, windowWidth) 
    : 
    createCanvas(windowHeight, windowHeight) ;

  colorMode(HSL, 1);
  noStroke();
}

function draw() {
  old_t = t;
  t = fract(frameCount / frames);

  if (old_t > t) {
    if (sceneID === 3) {
      sceneID = 1;
    }
    else {
      sceneID++;
    }
  }


  scale(width, height);
  background(0);


  const count = 2000 * inv_cosn(t);
  for (let i = 0; i < count; i++) {
      const divider = i/count;
      const a = i/PHI;
      const dist = divider * radius;
      const x = 0.5 + cos(a * TWO_PI) * dist; 
      const y = 0.5 + sin(a * TWO_PI) * dist;
      let sig;

      if (sceneID === 1) {
        sig = pow(cosn(divider * t * 12),2);
      }
      else if (sceneID === 2) {
        sig = pow(cosn(divider - t * 12),2);
      }
      else if (sceneID === 3) {
        sig = pow(cosn(divider + t * 12),2);
      }

      const r = sig * divider * dotSize;

      const hue = fract(divider * 0.25 + t);
      const sat = random(0.7,1);
      const light = 0.3 * sig + 0.2;

      fill(color(hue, sat, light));

      circle(x,y, r);
  }
}

function cosn(v) {
  return cos(v * TWO_PI) * 0.5 + 0.5;
}

function inv_cosn(v) {
  return 1 - cosn(v);
}

