function shape(numo, numt, frequency, amplitude, fillslid){


  fill(255,255,255, fillslid);

  beginShape();

  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    random()
    let offsetX = (noise(i*frequency + frameCount/numo)-.5) * amplitude;
    let offsetY = (noise(i*frequency + frameCount/numt)-.5) * amplitude;

    vertex(x + offsetX, y + offsetY)

  }
  endShape(CLOSE);
}

