let amplitudeSlider;
let frequencySlider;
let speedSlider;

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 50;

const pane = new Tweakpane.Pane();

const params = {
  max_freq: 0.1, 
  min_freq: 0.02,
  max_amp: 100,
  min_amp: 0,
  min_speed: 1,
  max_speed: 10,
  x_noise: 0,
  y_noise: 100,
  frame_clip: 100
};

function setup() {
  createCanvas(500, 300);

  createP("Frequency");
  frequencySlider = createSlider(0, 100, 50);
  createP("Amplitude");
  amplitudeSlider = createSlider(0, 100, 50);
  createP("Speed");
  speedSlider = createSlider(0, 100, 50);

  let keyNames = Object.keys(params);
  for(let i = 0; i < Object.keys(params).length; i++){
    pane.addInput(params, keyNames[i]);
  }
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let amplitude = map(amplitudeSlider.value(), 0, 100, params.min_amp, params.max_amp);
  let frequency = map(frequencySlider.value(), 0, 100, params.min_freq, params.max_freq);
  let speed = map(speedSlider.value(), 0, 100, params.min_speed, params.max_speed);

  drawValues(amplitude, frequency, speed);

  noiseDetail(1, 0.5);

  fill(255);
  noStroke();


  for (i = 0; i < 1; i += 0.02) {
    // as i increases the result of lerp moves toward the endX/Y values
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // variable for position depencence
    let position = i * frequency;

    // variable for time dependence
    let time = frameCount / params.frame_clip * speed;

    let offsetY = noise(position * time) * amplitude - 0.5;      
    let offsetX = noise(position * time) * amplitude - 0.5; 

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }

}

function drawValues(a, f, s){
  text(str(f), width - 100, height - 100);
  text(str(a), width - 100, height - 80);
  text(str(s), width - 100, height - 60);
}

