particles = [];

function setup() {
  createCanvas(CANVAS_WIDTH, CANVAS_HEIGHT);
  rectMode(CENTER);
  ellipseMode(CENTER);

  setTimeout(draw, 100); // wait for fonts to load, quick and dirty
}

function draw() {

  background(params.bgColor);

  for (let i = 0; i < 5; i++) {
    let p = new Particle();
    particles.push(p);
  }

  for (let i = particles.length - 1; i >= 0; i--) {
    particles[i].update();
    particles[i].show();
    if (particles[i].finished()) {
      particles.splice(i, 1);
    }
  }
}

class Particle {
  constructor() {
    this.x = params.posX;
    this.y = params.posY;
    this.vx = random(-(params.XAxis), params.XAxis);
    this.vy = random(-(params.YAxis), 1);
    this.alpha = 255;
  }

  finished() {
    return this.alpha < 0;
  }

  update() {
    this.x += this.vx;
    this.y += this.vy;
    this.alpha -= 5;
  }

  show() {

  const shapeFunction = window[params.shape];

    if(params.isStroke)
    {stroke(params.strokeColor);}
    else
    {noStroke();}

    fill(255, this.alpha);
    shapeFunction(this.x, this.y, params.size);
  }
}

