// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js

// draws a rectangle, where you tell it to!

let amplitude_slider;

let frequency_slider;

let time_slider;

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 50;

function setup() {
  createCanvas(500, 300);


  createP("Frequency");
  frequency_slider = createSlider(0, 100, 50);

  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);

  createP("Time Speed");
  time_slider = createSlider(0, 100, 50);
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let amplitude = amplitude_slider.value();
  let frequence = frequency_slider.value();
  let time = time_slider.value();

  noiseDetail(1, 0.5);

  fill(255);
  noStroke();

  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX = (noise(frameCount/20+time + i * frequence)) * amplitude * 1;
    let offsetY = (noise(i * frequence)) * amplitude * 1;

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js
// require https://cdn.jsdelivr.net/npm/tweakpane@3.0.7/dist/tweakpane.min.js

// mixing wang tiles (kinda) and bayer dithering

// inspired roughly by https://www.cs.tau.ac.il/~dcor/articles/2006/Recursive-Wang.pdf

// and this https://surma.dev/things/ditherpunk/

/* exported setup draw */
/* global Tweakpane */

const pane = new Tweakpane.Pane();
const params = {
  levels: 4,
};
const tiles = [
  [0, 0.25, 0.5, 0.75],
  [0.25, 0.75, 0.0, 0.5],
  [0.75, 0.5, 0.25, 0.0],
  [0.5, 0, 0.75, 0.25],
];
function setup() {
  pixelDensity(1);
  const c = createCanvas(320, 256);
  c.style("min-width", "640px");
  c.style("min-height", "512px");
  c.style("image-rendering", "pixelated");

  pane
    .addInput(params, "levels", { min: 2, max: 16, step: 1 })
    .on("change", draw);

  noLoop();
}

// eslint-disable-next-line complexity
function draw() {
  background(0);
  loadPixels();

  const column = 64;
  // draw noise
  for (let y = 0; y < height; y += 2) {
    for (let x = 0; x < column * 1; x += 2) {
      const tile = random(tiles);
      for (let b = 0; b < 2; b++) {
        for (let a = 0; a < 2; a++) {
          const n = tile[b * 2 + a];
          const q = roundTo(n * 255, 1);
          const c = [q, q, q, 255];
          setQuick(x + a, y + b, c);
        }
      }
    }
  }

  // draw smooth gradient
  for (let y = 0; y < height; y += 1) {
    for (let x = column * 1; x < column * 2; x += 1) {
      const g = y / height;
      const q = roundTo(g * 255, 1);
      const c = [q, q, q, 255];
      setQuick(x, y, c);
    }
  }

  // draw quantized gradient
  for (let y = 0; y < 512; y += 1) {
    for (let x = column * 2; x < column * 3; x += 1) {
      const g = y / height;
      const q = roundTo(g * 256, 256 / params.levels);
      const c = [q, q, q, 255];
      setQuick(x, y, c);
    }
  }

  // draw dithered gradient
  for (let y = 0; y < height; y += 2) {
    for (let x = column * 3; x < column * 4; x += 2) {
      const tile = random(tiles);
      for (let b = 0; b < 2; b++) {
        for (let a = 0; a < 2; a++) {
          const n = tile[b * 2 + a] / params.levels;
          const g = (y + b) / height;
          const q = roundTo((g + n) * 256, 256 / params.levels);
          const c = [q, q, q, 255];
          setQuick(x + a, y + b, c);
        }
      }
    }
  }

  // draw random dithered gradient
  for (let y = 0; y < height; y += 1) {
    for (let x = column * 4; x < column * 5; x += 1) {
      const n = random() / params.levels;
      const g = y / height;
      const q = roundTo((g + n) * 256, 256 / params.levels);
      const c = [q, q, q, 255];
      setQuick(x, y, c);
    }
  }

  updatePixels();
}

