//   return [
//     pixels[index + 0],
//     pixels[index + 1],
//     pixels[index + 2],
//     pixels[index + 3],
//   ];
// }

function setQuick(x, y, c) {
  const index = constrain((y * width + x) * 4, 0, width * height * 4);

  pixels[index + 0] = c[0];
  pixels[index + 1] = c[1];
  pixels[index + 2] = c[2];
  pixels[index + 3] = c[3];
}

function roundTo(n, q) {
  return floor(n / q) * q;
}

