class Kid {
  constructor(x, y) {
    this.startX = x;
    this.startY = y;
    // this.upY = 20;
    /*
     * 0: normal, bubble grow
     * 1: eye normal, left arm raise, bubble stay
     * 2: eye normal, left arm raise, bubble broke
     * 3: eye normal, left arm down
     * 4: eye normal, left arm down, bubble grow
     * 6: eye to normal, left arm down, bubble broke
     * 7: two arms down
     */
    this.state = 0;
  }

  setState(state) {
    this.state = state;
  }

  drawHead() {
    push();
    fill(255);
    ellipse(50, 30, 60, 60);
    pop();
  }

  drawEye(X) {
    push();
    if (this.state < 6) {
      fill(0, 0, 0);
      ellipse(X, 20, 5, 5);
    } else if (this.state < 11) {
      fill(0, 0, 0);
      ellipse(X - 20, 20, 5, 5);
    } else {
      arc(40, 30, 10, 10, 180, 0);
      arc(60, 30, 10, 10, 180, 0);
      line(X, -10, X, 20);
      line(X + 5, -5, X + 5, 15);
      line(X + 10, 0, X + 10, 15);
    }
    pop();
  }

  drawBody() {
    beginShape();
    vertex(70, 50);
    vertex(30, 50);
    vertex(10, 180);
    vertex(90, 180);
    endShape(CLOSE);
  }

  drawFeet() {
    if (this.state > 5) {
      line(30, 180, 30, 230);
      line(70, 180, 80, 230);
    } else {
      line(30, 180, 20, 230);
      line(70, 180, 70, 230);
    }
  }

  drawLeftArm(x, y) {
    push();
    translate(x, y);
    switch (this.state) {
      case 0:
        this.drawStick(60, 0);
        break;
      case 1:
      case 2:
      case 3:
        rotate(300);
        this.drawStick(60, 0);
        break;
      case 4:
      case 5:
        rotate(45);
        this.drawStick(60, 0);
        break;
      default:
        rotate(100);

        break;
    }

    line(0, 0, 60, 0);
    ellipse(60, 0, 10, 10);

    pop();
    if (this.state < 6) {
      this.drawBubble(x + 60, y);
    }
  }

  drawRightArm(x, y) {
    push();
    translate(x, y);
    if (this.state > 5) {
      rotate(115);
      this.drawStick(0, 60);
      this.drawBubble(0, 60);
    }

    line(0, 0, 0, 60);
    ellipse(0, 60, 10, 10);
    pop();
  }

  drawBubble(x, y) {
    push();
    translate(x, y);
    switch (this.state) {
      case 0:
        var size = 10 + frameCount;
        image(img, 40 - size / 2, -50 - size / 2, size, size);
        break;
      case 1:
      case 2:
        size = 100;
        image(img, 40 - size / 2, -50 - size / 2, size, size);
        break;
      case 4:
        size = 10 + frameCount - 150;
        image(img, 0, size / 2, size, size);
        break;
      case 5:
        size = 70;
        image(img, 0, size / 2, size, size);
        break;
      case 6:
      case 7:
      case 8:
      case 9:
      case 10:
      case 11:
        size = 10 + frameCount - 240;
        image(img, 0, size / 2, size, size);
        break;
    }
    pop();
  }

  drawStick(x, y) {
    push();
    translate(x, y);
    switch (this.state) {
      case 2:
        rotate(45);
        break;
      case 3:
        rotate(45);
        break;
      case 4:
        rotate(315);
        break;
      case 5:
        rotate(330);
        break;
      case 6:
        rotate(90);
        break;
    }
    if (this.state > 7) {
      rotate(60);
    }
    line(0, 0, 30, 0);
    pop();
  }

  draw() {
    push();
    translate(this.startX, this.startY);
    stroke(50);
    strokeWeight(3);

    this.drawFeet();
    this.drawBody();
    this.drawHead();
    this.drawEye(60);
    this.drawLeftArm(70, 80);
    this.drawRightArm(30, 80);
    pop();
  }
}

