let amplitude_slider;

let points = [
  { x: 50, y: 250, base: 10, r: 6, color: 50 },
  { x: 150, y: 200, base: 40, r: 10, color: 36 },
  { x: 380, y: 85, base: 60, r: 20, color: 23 },
];
let endX = 450;
let endY = 50;

function setup() {
  createCanvas(600, 400);
  // angleMode(DEGREES);
  colorMode(HSL);
  createP("frequency");
  frequency_slider = createSlider(0, 50, 100);
  createP("Amplitude");
  amplitude_slider = createSlider(0, 200, 100);
  createP("Time Speed");
  speed_slider = createSlider(0, 300, 150);
}

function drawBG() {
  c1 = color(250, 70, 10);
  c2 = color(230, 70, 60);

  for (let y = 0; y < height; y++) {
    n = map(y, 0, height, 0, 1);
    let newc = lerpColor(c1, c2, n);
    stroke(newc);
    line(0, y, width, y);
  }
}

function star(x, y, radius1, radius2, npoints) {
  let angle = TWO_PI / npoints;
  let halfAngle = angle / 2.0;
  beginShape();
  for (let a = 0; a < TWO_PI; a += angle) {
    let sx = x + cos(a) * radius2;
    let sy = y + sin(a) * radius2;
    vertex(sx, sy);
    sx = x + cos(a + halfAngle) * radius1;
    sy = y + sin(a + halfAngle) * radius1;
    vertex(sx, sy);
  }
  endShape(CLOSE);
}

function draw() {
  drawBG();
  translate(20, 80);
  let amplitude = amplitude_slider.value() / 100;
  let frequency = frequency_slider.value() / 2000;
  let timeSpeed = speed_slider.value() / 2000;
  noiseDetail(1, 0.1);

  push();
  fill(40, 100, 360);
  star(120, -30, 1, 6, 5);
  star(20, 40, 3, 10, 7);
  star(180, 120, 1, 7, 5);
  star(400, 240, 1, 8, 5);
  star(480, -60, 1, 8, 4);
  pop();

  noStroke();
  for (j = points.length - 1; j >= 0; j--) {
    for (i = 0; i < 1; i += 0.01) {
      let x = lerp(points[j].x, endX, i);
      let y = lerp(points[j].y, endY, i);

      let offsetX =
        noise(i * 20 * frequency, timeSpeed * frameCount * 0.5) *
        amplitude *
        10;
      let offsetY =
        noise(i * 20 * frequency, timeSpeed * frameCount) * amplitude * 10;

      push();
      translate(x, y);
      // rotate(-50);
      fill(points[j].color, 100, 60, i * 0.1);
      ellipse(
        offsetX,
        offsetY,
        points[j].base + random(i * points[j].r),
        points[j].base + random(i * points[j].r)
      );
      if (j == 0) {
        ellipse(
          offsetX,
          offsetY,
          points[j].base * 2 + random(i * points[j].r * 2),
          points[j].base * 2 + random(i * points[j].r * 2)
        );
        ellipse(
          offsetX,
          offsetY,
          points[j].base * 3 + random(i * points[j].r * 3),
          points[j].base * 3 + random(i * points[j].r * 3)
        );
      }
      pop();
    }
  }
}

