let startX, startY;
let halflength;
let counter;
let decrease = false;

//setUp background 
let inc = 0.02;
let scl = 2;
let cols, rows;
let zOff = 0;

function setup() {
    createCanvas(800, 600);
    // colorMode(RGB, 255, 255, 255, 1);
    createCanvas(600, 600);
    background(0, 0, 0);

    cols = floor(width / scl);
    rows = floor(height / scl);
    colorMode(HSB, 360, 100, 100, 1);

    noLoop();
    myTurtle = new Turtle();
    angleRad = degrees_to_radians(angle);
    halfShapeOuterBox = sin(angleRad) * sideLength;
    shapeOuterBox = 2 * halfShapeOuterBox;
}

function draw() {
    push();
    drawBackground();
    pop();
    myTurtle.penUp();
    stroke(255, 255, 255);
    startX = 0;
    startY = -300;
    myTurtle.moveTo(startX, startY);
    myTurtle.turnTo(90);
    myTurtle.penDown();
    h = 42;
    s = 50;
    b = 0;

    counter = 0;
    for (let i = 0; i < 40; i++) {
        draw1Column();
        startX += halfShapeOuterBox * 3;
        startY += sideLength * 1.5;
        console.log(counter);
    }

}

function drawBackground() {
    var yOff = 0;
    for (let y = 0; y < rows; y++) {
        var xOff = 0;
        for (let x = 0; x < cols; x++) {
            let index = (x + y * width) * 4;
            let brightness = noise(xOff, yOff, zOff) * 255;
            setColors(brightness);

            //update brightness
            // if (brightness > treasureheight) {
            //     treasureheight = brightness;
            //     treasurePos = {
            //         x: x * scl,
            //         y: y * scl,
            //     };
            // }
            xOff += inc;
            rect(x * scl, y * scl, scl, scl);
        }
        yOff += inc;
        zOff += 0.010;
    }
}

function draw1Column() {
    for (let i = 0; i < 25; i++) {
        stroke(h, s, b);
        makeHoneyComb();

        if (b >= 100) {
            decrease = true;
        }

        if (decrease == true) {
            b -= .4;
        } else {

            b += .4;
        }
    }
    counter++;
    myTurtle.penUp();
    myTurtle.turnTo(90);
    if (counter % 2 == 0) {
        myTurtle.moveTo(startX + (halfShapeOuterBox * 3), startY + (sideLength * 1.5));
        console.log("down");
    } else {
        console.log("up");
        myTurtle.moveTo(startX + (halfShapeOuterBox * 3), startY - (sideLength * 1.5));
    }

}


function makeHoneyComb() {

    myTurtle.moveForward(sideLength * 2);
    myTurtle.turnLeft(60);
    myTurtle.penDown();
    //make how many j"s random so ir ends up in different places 
    for (let j = 0; j < 5; j++) {

        for (let i = 0; i < 5; i++) {

            myTurtle.turnRight(60);
            myTurtle.moveForward(sideLength);
        }
        myTurtle.turnRight(300);
        myTurtle.moveForward(sideLength);
        for (let i = 0; i < 5; i++) {

            myTurtle.turnLeft(60);
            myTurtle.moveForward(sideLength);
        }
    }
    myTurtle.penUp();
}

function radians_to_degrees(radians) {
    var degrees = radians * (180 / Math.PI);
    return degrees;
}

function degrees_to_radians(degrees) {
    const radians = degrees * (Math.PI / 180);
    return radians;
}


function setColors(brightness) {

    h = 35;
    s = map(brightness, 0, 255, 0, 100);
    // s = 30;
    b = map(brightness, 0, 200, 0, 100);

    fill(h, s, b);
}

