var myTurtle;
let lineInc, lineLength;
let strokeWeightFactor;

function setup() {
    createCanvas(800, 800);
    colorMode(RGB, 255, 255, 255, 1);
    // stroke(255);
    background(0);
    noLoop();
    myTurtle = new Turtle();
}


function draw() {

    for (let i = 0; i < 50; i++) {
        var deg = int(random(0, 360));
        myTurtle.penUp();
        push();
        myTurtle.moveTo(width / 2, height / 2);
        rotate(deg);
        // drawNeuronPair(width / 2, height / 2);
        drawNeuronPair(random(800), random(800));
        pop();
    }

}

function drawNeuronPair(initialX, initialY) {

    myTurtle.turnTo(90);
    let neuronSpacing = 25;
    myTurtle.moveTo(initialX, initialY);
    // myTurtle.turnTo(150);
    myTurtle.penDown();
    drawNeuron(myTurtle, 25);
    myTurtle.penUp();
    myTurtle.turnTo(90);
    myTurtle.turnRight(random(150, 190));
    myTurtle.moveTo(initialX - neuronSpacing, initialY + neuronSpacing);
    // myTurtle.turnTo(150);
    myTurtle.penDown();
    drawNeuron(myTurtle, 25);
    noLoop();

}


function drawNeuron(turtle, size) {
    strokeWeight(size);
    lineInc = 2;
    lineLength = 30;
    stroke(0, 0, random(100, 200), .03);
    for (var i = 0; i < 90; i++) {
        turtle.moveForward(random(.6));
        turtle.turnRight(random(.8, 2));
    }
    drawNeuronStem(turtle, size);
}

function drawNeuronStem(turtle, size) {
    stroke(0, 0, 250, .25);
    myTurtle.moveForward(lineInc);
    for (let i = lineInc; i < lineLength; i++) {
        // size -= map(i, 0, lineLength, 0, size);
        if (size > 5) {
            size -= 1;
        }
        // stroke(252, 250, 250, .1);
        stroke(0, 0, random(100, 255), .3);
        strokeWeight(size);
        turtle.turnRight(random(.8, 1));
        myTurtle.moveForward(lineInc);
    }
    strokeWeightFactor = 10;
    drawBranch(50, .55);
}

function drawBranch(length, opacity) {
    stroke(0, 0, random(100, 255), opacity);
    // stroke(252, 250, 250, opacity);
    // console.log(opacity);
    if (length < 10) {
        return;
    }
    // draw this branch
    strokeWeightInc = .3;

    strokeWeight(length / strokeWeightFactor);
    strokeWeightFactor += strokeWeightInc;
    myTurtle.turnLeft(1);
    myTurtle.moveForward(length * random(.6, 1));

    myTurtle.moveForward(length * random(.6, 1.2));
    // left child
    myTurtle.pushState();
    myTurtle.turnLeft(random(2, 10));
    drawBranch(length * random(.6, 1), .91);
    //drawBranch(length * random(.5, .9));
    myTurtle.popState();
    // right child
    myTurtle.pushState();
    myTurtle.turnRight(random(10, 30));
    drawBranch(length * random(.6, 1), 1);
    //drawBranch(length * random(.5, .9));
    myTurtle.popState();

}

// TREASURE MAP WITH TERRAIN GENERATOR
// For logic and notes, view logic_notes.md file on GitHub: https://github.com/akshansh2593/CompForm/blob/master/terrainGenerator/logic_notes.md

// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.7.3/p5.js

// creating a grid
var cols; // columns
var rows; // rows
var scl = 20; // scale
var w = 1200; // width
var h = 900; // height

// 2D array for storing grid values (x and y)
var terrain = [];

// variable to make the grid move
var flying = 0;

// CUSTOMIZE
var terrainHeight_slider; // change terrain height
//var terrainColor;


function setup() {
    createCanvas(600, 600, WEBGL);

    // grid proportion
    cols = w / scl;
    rows = h / scl;

    // Store the x and y grid values in an array
    for (var x = 0; x < cols; x++) {
        terrain[x] = [];
        for (var y = 0; y < rows; y++) {
            terrain[x][y] = 0;
        }
    }


    // CUSTOMIZE
    createP('Change Altitude');
    terrainHeight_slider = createSlider(20, 300, 160, 20);

}

function draw() {
    background(177, 219, 237); // sky

    // Treasure spot
    fill(255, 0, 0);
    push();
    translate(-width, -height);
    ellipse(width, height, 20, 20);
    pop();

    fill(151, 124, 83); // terrain background
    // noStroke();
    stroke(0); // terrain edges
    // noFill();
    // terrainColor = map(noise(-terrainHeight_slider.value(), terrainHeight_slider.value()), 0, 1, 0, 255);


    //flying -= 0.2;

    // Build the terrain
    var yOff = flying;
    for (var y = 0; y < rows; y++) {
        var xOff = 0;
        for (var x = 0; x < cols; x++) {
            terrain[x][y] = map(noise(xOff, yOff), 0, 1, -terrainHeight_slider.value(), terrainHeight_slider.value());
            xOff += 0.2;
        }
        yOff += 0.2;
    }

    // Center align the grid
    translate(0, 50);
    rotateX(PI / 3);
    translate(-w / 2, -h / 2);

    // Create the grid
    for (var y = 0; y < rows - 1; y++) {
        beginShape(TRIANGLE_STRIP);
        for (var x = 0; x < cols; x++) {
            vertex(x * scl, y * scl, terrain[x][y]);
            vertex(x * scl, (y + 1) * scl, terrain[x][y + 1]);
        }
        endShape();
    }
}

