var amplitude_slider, speed_slider, frequency_slider;
var active = 0;
var startX = 70;
var startY = 40;
var endX = 400;
var endY = 40;
var amplitude = .4;


function setup() {
    createCanvas(500, 300);


    createP('time speed');
    speed_slider = createSlider(10, 100, 40);
    createP('frequency');
    frequency_slider = createSlider(10, 60, 40);

}


function draw() {
    background(15, 34, 56);
    // rectMode(CENTER);

    var speed = speed_slider.value() / 1000;
    var frequency = frequency_slider.value() / 5;
    active = speed + active;
    noiseDetail(1, .5);

    for (i = 0; i < 1; i += .02) {
        var x = lerp(startX, endX, i);
        var y = lerp(startY, endY, i);
        var offsetX = noise(frequency*i + active) * amplitude * 80;
        var offsetY = noise(frequency *i + active) * amplitude * 80;

