const w = window.innerWidth;
const h = w;
const inc = 0.01;

function setup() {
  createCanvas(w, h);
  // colorMode(HSB, 1, 1, 1, 1);
  angleMode(DEGREES);
  // rectMode(CENTER);
  pixelDensity(1);

  window.canvasColor = color(226, 226, 226);
