//require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.7.3/p5.min.js
let time = 0;
let curlWidth = 40;
let frequency = 0.15; //frequency //how fast they rotate 
let pointTightness = 1;
let pointDiameter = 2;


function setup() {
    createCanvas(600, 600);
    background(0, 0, 0);
    smooth();

    colorMode(RGB, 255, 255, 255, 1);

    // stroke(255, 102, 0, 1);
    fill(255, 102, 0);
    noStroke();


    for (tuft = 1; tuft < 15; tuft++) {
        //starting point for a tuft of hair 
        origin = tuft * 40;
        tuftLength = Math.floor(Math.random() * 100) + 500;


        // width  is length of strands 
        for (strands = 0; strands < 50; strands++) {
            let red = Math.floor(50 * Math.random() + 100);
            let green = Math.floor(20 * Math.random() + 40);
            let opacity = Math.random() - .2;
            fill(red, green, 10, opacity);
            length = tuftLength + (Math.random() * 70);
            originDeviation = (Math.random() * 30) + 5;
            strandOriginX = origin + originDeviation;
            curlTightnessDeviation = random(.04, .045);
            curlWidthDeviation = Math.floor(Math.random() * 15) + 25;
            // curlWidth += curlWidthDeviation;
            //single strand of hair
            for (i = 0; i < length; i += pointTightness) {

                //bigger phase, the less curcles 
                let curlTightness = i * curlTightnessDeviation;
                ellipse(strandOriginX + curlWidth + curlWidthDeviation * sin(frequency + curlTightness), i, pointDiameter, pointDiameter);

            }
        }
    }
}

