// Draws concentric circles with noise(), beginShape(), endShape().
// Refresh the sketch for a new image.
// Credits:
// - makeRing() function from https://www.youtube.com/watch?v=76fiD5DvzeQ.

const canvas = {
    w: 800,
    h: 800
};

function setup() {
    createCanvas(canvas.w, canvas.h);

    // line and canvas aesthetics
    stroke(0, 0, 85);
    background(250, 250, 250);
    noFill();
    strokeWeight(.15);
    angleMode(DEGREES);

    makeShape(300);
}

function makeRing(radius) {
    // Uses https://www.youtube.com/watch?v=76fiD5DvzeQ, adds noise
    beginShape();
    for (let a = 0; a < 360; a += 1) {
        let x = radius * sin(a) + canvas.w / 3;
        let y = radius * cos(a) + canvas.h / 3;
        let n = noise(x * .01, y * .001)
        vertex(x + (radius * .7) * n, y + (radius * .7) * n);
    }
    endShape(CLOSE);
}

function makeShape(numRings) {
    for (let i = 1; i < numRings; i += 3) {
        makeRing(i);
    }
}

