}

function draw() {
  background(0);
  var xoff = 0.0;
  var yoff = 0.0;
  for (var y = 0; y < height; y += density) {
    for (var x = 0; x < width; x += density) {
      var r = noise(xoff, yoff, zoff) * 255;
      var g = noise(xoff, yoff, zoff) * 100;
      fill(r, g, 255);
      rect(y, x, density, density);
      xoff += inc;
    }
    xoff = 0;
    yoff += inc;
  }
  zoff += inc;
}

