// Truchet tiles

let debugging = false; // true turns on logging

// Canvas specs
const canvas_width = 700,
      canvas_height = canvas_width,
      num_cols = 10,
      num_rows = num_cols,
      tile_width = canvas_width / num_cols,
      stroke_color = "#0b53bf",
      stroke_weight = 5,
      background_color = "#9aafc7",
      rows = [],            // An array of row arrays, retrievable by row number
      tilesOnThisRow = [];  // An array of Tile objects, retrievable by col number;


function setup() {
  const canvas = createCanvas(canvas_width + 2, canvas_height + 2);
  canvas.parent("sketch-container");
  stroke(stroke_color);
  strokeWeight(stroke_weight);
  background(background_color);
  noFill();

  for (row = 0; row < num_rows; row++) {
    for (col = 0; col < num_cols; col++) {
      tilesOnThisRow[col] = new Tile(col, row); // Add new tile to row
      tilesOnThisRow[col].layTile();
      rows[row] = tilesOnThisRow; // Add row to array of rows
    }
  }
  if (debugging) { console.log(rows); }
}

class Tile {
  constructor(col, row) {
    this.x = col * tile_width;
    this.y = row * tile_width;
    this.rand_chooser = -1 + random(2); // rand num btwn 0 and 1
  }

  // TODO: clean methods
  layTile() {
    if (this.rand_chooser > 0) {
      // Top left
      arc(
        this.x + tile_width, this.y,
        tile_width, tile_width,
        PI / 2, -PI
      );
      // Bottom right
      arc(
        this.x, this.y + tile_width,
        tile_width, tile_width,
        -PI / 2, 0
      );
    } else {
      // Top right 
      arc(
        this.x, this.y,
        tile_width, tile_width,
        0, PI / 2
      );
      // Bottom left
      arc(
        this.x + tile_width, this.y + tile_width,
        tile_width, tile_width,
        -PI, -PI / 2
      );
    }
  }
}

