function generateTitle() {
	var name_prefixes = ["lowlifes", "Losers", "Total loser", "Morons", "Facelifts", "Big Beautiful Wall",  "Cyber", "Dummies",];
	var primary_nouns = ["Covfefe", "Chai-Na", "Loser", "Fire and Fury", "Fake News", "Billions and Billions of Dollars", "Fox News", "illegals"];
	var adjectives = ["Crooked", "YUUUGE", "Bigly", "many, many, many", "Terrific", "Tremendous", "Illegal", "very very happy", "Loser", "Phenomenal"];
	var secondary_nouns = ["Loser", "Terrorist", "Immigrant", "Chai-Na", "Covfefe", "Fake News"];

	var name_prefix = sample(name_prefixes);
	var primary_noun = sample(primary_nouns);
	var adjective = sample(adjectives);
	var secondary_noun = sample(secondary_nouns);

	var title = "";
	if (Math.random() < 0.5) {
		title = `${name_prefix} ${primary_noun} and the ${adjective} ${secondary_noun}`;
	}
	else {
		title = `The ${adjective} ${secondary_noun} of ${name_prefix} ${primary_noun}`;
	}

	return title;
}

function generateSubtitle() {
	var story_adjectives = ["Totally Lost", "Forgotten", "Very very Happy", "Massive", "YUUGE", "GREAT", "Very, very Bad", "Incredible", "Truly great", "Unbelievable", "major, major", "Vast vast" ];
	var story_formats = ["Story, believe me.", "Fiasco, never seen before, ever.", "Super-luxury, I have many many friends.", "Law, not gonna happen folks.", "Impeachment, to be perfectly honest.", "Collusion, believe me. ", "Wall, that I can tell you.", "Tweetâ€¦ okay?", "Lie, A lot of people tell me â€¦", "Disaster, Everybodyâ€™s talking about it.","Disaster, The Likes of which have never been seen before."];

	var adjective = sample(story_adjectives);
	var format = sample(story_formats);
	var subtitle = `A  ${adjective}  ${format}`;

	return subtitle;


}


function sample(array) {
	const index = Math.floor(Math.random() * array.length);
	return array[index];
}




