function myUpdate() {
  var spectrum = fft.analyze();
  var smooth = fft.smooth(0.4);
  // console.log(fft.getEnergy("bass"));
  var bass = parseInt(fft.getEnergy("bass"));
  var mapBass = map(bass, 0, 255, 180, 210);
  var mid = parseInt(fft.getEnergy("mid"));
  var mapMid = map(mid, 0, 255, 0, 90);
  var treble = parseInt(fft.getEnergy("treble"));
  var mapTreble = map(treble, 0, 255, 270, 300);
  // console.log(mid, mapMid, treble, mapTreble);

  push()
  var levelL = amplitude.getLevel(0);
  var hueLevelL = map(levelL, 0, 0.5, 0, 360);
  var levelR = amplitude.getLevel(1);
  var hueLevelR = map(levelR, 0, 0.5, 360, 0);
  let acc = sin(frameCount*0.01)
  let accMap = map(acc, -1, 1, -2*PI, 2*PI)
  translate(w/2, h/2)
  rotate(mapBass*0.5)
  translate(-w/2, -h/2)
  makeCircles(140, w/2, h/2, mapBass + mapMid, 70 + mapTreble);
  pop()
  beginShape();
  for (i = 0; i < spectrum.length; i++) {
    vertex(i, map(spectrum[i].ctr, 0, 255, height, 0));
  }
  endShape();
}

function makeCircles(num, x, y, startHue, endHue) {
  for (let i = 0; i < num; i += circleDistance) {
        makeCircle(i, x, y, startHue, endHue, num);
    }
}

function makeCircle(radius, xP, yP, startHue, endHue, num) {
  let circum = 2 * PI * radius
  for (let a = 0; a < circum/radius - circum/radius*0.1/num; a += PI/radius) {
    let x = radius * sin(a) + xP;
    let y = radius * cos(a) + yP;
    let hue = map(a, 0, 2*PI, startHue, endHue)
    fill(hue, 1, 1, 0.2)
    ellipse(x, y, circleSize, circleSize)
  }
}

