// Creating a word map using RiTa.js in p5.js.
// An extension to the tactic of placing random dots on the screen

// For notes, refer notes.md file
// GitHub link: https://github.com/akshansh2593/CompForm/blob/master/wordMap/notes.md


// Basic Stuff
var wordCount;
var lexicon; // Variable to store reference to the lexicon
var hueValue; // vary the Hue value
var colorNoise;

// Customization
var changeSyllable_button;
var syllableCount_slider;
var partsOfSpeech_dropdown;
var partOfSpeech_pennTag;


function setup() {
    createCanvas(500, 500);

    wordCount = 50;
    lexicon = new RiLexicon();


    createP('Change the number of syllables (1-5)');
    syllableCount_slider = createSlider(1, 5, 1, 1);
    syllableCount_slider.changed(randomizeWord);

    createP('Change the part of speech');
    partsOfSpeech_dropdown = createSelect();
    partsOfSpeech_dropdown.option("Noun"); // noun
    partsOfSpeech_dropdown.option("Verb"); // verb
    partsOfSpeech_dropdown.option("Adjective"); // adjective
    partsOfSpeech_dropdown.option("Adverb"); // adverb
    partsOfSpeech_dropdown.changed(randomizeWord);

}


function draw() {

    randomizeWord(); // run by default
    changeSyllable_button.mousePressed(randomizeWord); // change when mousePressed

    noLoop();
}



// Arrangement
function randomizeWord() {
    background(0);
    colorMode(HSB);
    var noiseFrequency = 10; // play with this value

    for (i = 0; i < wordCount; i++) {
        noiseSeed(1);
        var xPos = noise(i * noiseFrequency) * width;
        noiseSeed(10002);
        var yPos = noise(i * noiseFrequency) * height;

        // Color
        hueValue = random(0, 360);
        var colorNoise = noise(hueValue * noiseFrequency) * 360;
        fill(colorNoise, 100, 100);

        // Font Size
        var fontSize = map(colorNoise, 0, 360, 4, 20);
        textSize(fontSize);

        // add a random word at every position

        // condition for parts of speech (change words to PENN tags)
        if (partsOfSpeech_dropdown.value() === "Noun") {
            partOfSpeech_pennTag = "nn";
        }
        if (partsOfSpeech_dropdown.value() === "Verb") {
            partOfSpeech_pennTag = "vb";
        }
        if (partsOfSpeech_dropdown.value() === "Adjective") {
            partOfSpeech_pennTag = "jj";
        }
        if (partsOfSpeech_dropdown.value() === "Adverb") {
            partOfSpeech_pennTag = "rb";
        }

        text(lexicon.randomWord(partOfSpeech_pennTag, syllableCount_slider.value()), xPos, yPos);

    }
}

// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/p5.js

// Turns the intro of Treasure Island into a treasure map, where
// a red x marks the spot.
// Text from https://www.gutenberg.org/files/120/120-h/120-h.htm.

const noiseOn = true; // toggle for debugging
const w = 550, h = 700, border = {x: 100, y: 50};
const fnt = "Courier", fntSize = 5; // min 5
let lines;
let letters = [];

function preload() {
    lines = loadStrings("https://raw.githubusercontent.com/annagarbier/compform/master/treasure_island.txt");
}

function setup() {
    createCanvas(w, h);
    background(224, 220, 212, 100);
    fill(114, 110, 102);
    textFont(fnt);
    textSize(9);

    makeLetterArray();
    drawLetterGrid();
}

function makeLetterArray() {
    let txtNorm = join(lines, "").replace(/[^A-Za-z0-9]/g,"");
    letters = txtNorm.split("");
}

// noisy position
function drawLetterGrid() {
    let letterIndex = 0;
    translate(-border.x / 2, -border.y / 2);
    for (let y = border.y; y < h - border.y; y += fntSize + 1) {
        for (let x = border.x; x < w - border.x; x += fntSize + 1) {
            // choose letter
            let letter = letters[letterIndex];

            push();
            let n = 0;

            // Toggle (x, y, z) noise for debugging purposes
            if (noiseOn) {
                n = noise(x * .01, y * .01, millis() * .0005);
            }
            let noisyPos = {
                x: x + n * 80,
                y: y + n * 80
            };

            // text formatting
            if (letter === "x") {
                textStyle(BOLD);
                textSize(fntSize + 5);
                fill(255, 0, 0);
            }

            // style the edge of the map
            let cntr = {
                x: w / 2 + border.x,
                y: h / 2 + border.y
            }
            let distToCntr = dist(noisyPos.x, noisyPos.y, cntr.x, cntr.y);

            // draw the letter
            if (distToCntr < w / 3 + n * 200) {
                text(letter, noisyPos.x, noisyPos.y);
            }
            pop();

            // if we've reached the end of the letters array, repeat
            // else move to the next letter.
            if (letterIndex === letters.length) {
                letterIndex = 0;
            } else {
                letterIndex++;
            }
        }
    }
}

