function getColorx(val) {

	var x = map(noise(xoff, yoff), 0, 1, val, 200);
	return x;
}

function getColory() {
	var y = map(noise(xoff, yoff), 0, 1, 50, 150);
	return y;
}

function assignValues(value) {

	for (let x = 0; x < width / stepSize; x++) {
		spots[x] = [];
		cols[x] = []; // create nested array
		for (let y = 0; y < height / stepSize; y++) {
			// let rand = Math.floor(random(0, 7));

			//cleanup-----------
			let rand;

			if (value < 100) {
				rand = Math.floor(map(getColorx(value), value, 100, 0, 6));
			} else if (value > 100) {
				rand = Math.floor(map(getColorx(value), value, 200, 6, notes.length));
				}else{
				rand = Math.floor(random(0, 7));
				}
			spots[x][y] = {
				colr: color(getColorx(value), getColory(), 100),
				//map it
				note: notes[rand]

			};

			xoff += 0.05;
		}
		yoff += 0.01;
	}



}

