let img;
const canvasWidth = 500;
const canvasHeight = 500;
const pixelWidth = 16;
const pixelHeight = 4;
const activeArray = new Array(pixelWidth * pixelHeight).fill(0);
activeArray[22] = 1;


function preload() {
  hat = loadSound('http://compform.net/sound/sketches/hat.wav');
  kick = loadSound('http://compform.net/sound/sketches/kick.wav');
  snare = loadSound('http://compform.net/sound/sketches/snare.wav');
  clap = loadSound('http://compform.net/sound/sketches/clap.wav');
  window.samples = [hat, kick, snare, clap];

}


function setup() {
  createCanvas(canvasWidth, canvasHeight);
  createP("Speed");
  window.speedSlider = createSlider(0, 20, 2, 0.5);
  colorMode(HSB, 1, 1, 1, 1);
  angleMode(DEGREES);
  noSmooth();
  img = createImage(pixelWidth, pixelHeight);
}

function draw() {
  frameRate(60);
  background(0);
  drawGradient();
  drawActivePixels();
  indicatorLine.updateAndPlay();
}

var indicatorLine = {
  x: 75,
  y1: 0,
  y2: canvasHeight,
  currentY: [],
  draw() {
    line(this.x, this.y1, this.x, this.y2);
  },
  move() {
    this.x += speedSlider.value();
    if (this.x > canvasWidth) {
      this.x = 0;
    }
  },
  updateXPixel() {
    xPixel = positionToPixel(this.x, 0);
    return xPixel.x;
  },
  checkAgainstActiveArray() {
    this.currentY = [];
    for (let y = 0; y < pixelHeight; y++) {
      yPos = pixelToPosition(this.x, y);
      pixel = positionToPixel(this.x, yPos.y1);
      arrayIndex = pixelToArray(pixel.x, pixel.y);
      this.currentY.push(arrayIndex);
    }
    // console.log(this.currentY);
  },
  playSound() {
    this.checkAgainstActiveArray();
    for (let y = 0; y < this.currentY.length; y++) {
      if (activeArray[this.currentY[y]] == 1) {
        samples[y].play()
      }
    }
  },
  updateAndPlay() {
    this.move();
    this.draw();
    this.playSound();
  }
};

function mouseClicked() {
  if (
    mouseX > 0 &&
    mouseX < canvasHeight &&
    mouseY > 0 &&
    mouseY < canvasHeight
  ) {
    mousePixel = identifyMousePositionPixel();
    onOffArray(mousePixel.x, mousePixel.y);
    drawActivePixels();
    indicatorLine.checkAgainstActiveArray();
  }
}

function drawGradient() {
  img.loadPixels();
  for (var y = 0; y < img.height; y++) {
    for (var x = 0; x < img.width; x++) {
      var oscA = sin(x + y);
      var hue = ((oscA + 1) / 2)*10  % 0.5;
      var c = color(0.5, hue, hue);
      img.set(x, y, c);
    }
  }
  img.updatePixels();
  image(img, 0, 0, width, height);
}

function drawActivePixels() {
  img.loadPixels();
  for (let i = 0; i < activeArray.length; i++) {
    if (activeArray[i] == 1) {
      var pixelPos = arrayToPixel(i);
      img.set(pixelPos.x, pixelPos.y, color(0, 0, 1, 1));
    } else {
      var pixelPos = arrayToPixel(i);
      img.set(pixelPos.x, pixelPos.y, color(0, 0, 0, 0));
    }
  }
  img.updatePixels();
  image(img, 0, 0, width, height);
}

function onOffArray(x, y) {
  index = pixelToArray(x, y);
  if (activeArray[index] == 0) {
    activeArray[index] = 1;
  } else {
    activeArray[index] = 0;
  }
  // console.log(activeArray);
}

function pixelToArray(x, y) {
  index = y * pixelWidth + x;
  return index;
}

function arrayToPixel(index) {
  xPos = index % pixelWidth;
  yPos = floor(index / pixelWidth);
  return { x: xPos, y: yPos };
}

function positionToPixel(x, y) {
  var xPixel = 0;
  if (x > canvasWidth) {
    xPixel = pixelWidth - 1;
  } else if (x < 0) {
    xPixel = 0;
  } else {
    xPixel = floor((x / canvasWidth) * pixelWidth);
  }
  var yPixel = 0;
  if (y > canvasHeight) {
    yPixel = pixelHeight - 1;
  } else if (y < 0) {
    yPixel = 0;
  } else {
    yPixel = floor((y / canvasHeight) * pixelHeight);
  }
  return { x: xPixel, y: yPixel };
}

function pixelToPosition(x, y) {
  var xPos1 = x * (canvasWidth / pixelWidth);
  var xPos2 = (x + 1) * (canvasWidth / pixelWidth);
  var yPos1 = y * (canvasHeight / pixelHeight);
  var yPos2 = (y + 1) * (canvasHeight / pixelHeight);
  return { x1: xPos1, x2: xPos2, y1: yPos1, y2: yPos2 };
}

function identifyMousePositionPixel() {
  return positionToPixel(mouseX, mouseY);
}

