function draw() {
    moveTurtle();
    noLoop();
}

function moveTurtle() {
    stroke(255);
    T.penUp();
    T.moveTo(anchor.x, anchor.y);
    T.turnRight(90);
    T.penDown();
    push();
    drawTentacle();
    drawShortTentacles();
    drawBody();
    pop();

    for (let i = 0; i < 3; i++) {
        // translate(150, 100);
        translate(random(-400, 400), random(-100, 200));
        push();
        drawTentacle();
        drawShortTentacles();
        drawBody();
        pop();
    }

}

function drawTentacle() {
    T.pushState();
    const numTentacles = 100;
    const numSegmentsPerTentacle = 1000;
    for (let i = 0; i < numTentacles; i++) {
        T.penUp();
        T.moveTo(
            anchor.x + (random(-40, 40)),
            anchor.y + (random(-10, 10))
        );
        T.penDown();

        let tentacleLength = random(400, 525);
        stroke(232, 236, random(244, 250), random(5, 10));
        // stroke(3, 57, random(102, 120), random(10, 40));
        T.pushState();
        let tentacleWeight = min(random(.6, 3), random(.6, 3), random(.6, 3));

        // main tentacles
        for (let j = 0; j < numSegmentsPerTentacle; j++) {
            let segmentLength = tentacleLength / numSegmentsPerTentacle;
            tentacleWeight -= .001;
            strokeWeight(tentacleWeight);

            // curve at top
            if (j < numSegmentsPerTentacle * .15) {
                T.turnRight(random(-.4, .4));
            }

            // more curve at end
            if (j > numSegmentsPerTentacle * .85 + random(.5)) {
                T.turnRight(random(-.1, .3));
            }

            T.turnRight(random(-.5, .5));
            T.moveForward(segmentLength);

            // decoration at very end
            if (j === numSegmentsPerTentacle - 1) {
                T.pushState();
                for (let k = 0; k < 5; k++) {
                    stroke(3, 57, random(102, 120), random(10, 40));
                    T.turnRight(random(80));
                    T.moveForward(.2);
                }
                T.popState();
            }
        }
        T.popState();
    }
    T.popState();
}

function drawShortTentacles() {
    T.pushState();
    const numTentacles = 40;
    const numSegmentsPerTentacle = 500;
    for (let i = 0; i < numTentacles; i++) {
        T.penUp();
        T.moveTo(
            anchor.x + (random(-40, 40)),
            anchor.y + (random(-10, 10))
        );
        T.penDown();
        const tentacleLength = random(100, 250);
        // stroke(51, 40, 76, random(10, 40));
        stroke(232, 236, 244, random(5, 10));
        T.pushState();
        let tentacleWeight = min(random(.6, 2), random(.6, 2), random(.6, 2));

        // main tentacles
        for (let j = 0; j < numSegmentsPerTentacle; j++) {
            let segmentLength = tentacleLength / numSegmentsPerTentacle;
            tentacleWeight -= .0001;
            strokeWeight(tentacleWeight);

            T.turnRight(random(-1.4, 1.4));
            T.moveForward(segmentLength);
        }
        T.popState();
    }
    T.popState();
}

function drawBody() {
