    createCanvas(canvas_width, canvas_height);
    colorMode(HSB, 1);
    background(.1, .001, .95);

    let y_start = drawing_border;
    let y_end = random(200, 300);

    while (y_end < canvas_height - drawing_border) {
        drawLines(y_start, y_end);
        y_start = y_end;
        y_end += random(50, 300);
    }
